<?php/** * Categories *  * @package UPcms\Catalog * @author UP Studio <info@up-studio.net> * @date 2015-03-06 */class Category {        /**     * Show all     *      * @param integer $id - ID parent's category     * @return array     */    public static function show($id) {        $sql = "SELECT * FROM `categories` WHERE `id_parent` = $id ORDER BY `sort`";        return Db::doArray($sql);    }        /**     * Add     *      * @param integer $id - parent ID     * @param string $name - name     * @param string $text - about     * @param string $img - name of image     * @param string $title - Title     * @param string $description - Description     * @param string $keywords - Keywords      * @return boolean     */    public static function add($id, $name, $text, $img, $title, $description, $keywords) {             $sql = "INSERT INTO `categories`                     SET `id_parent` = $id,                       `name` = '$name',                       `text` = '$text',                       `img` = '$img',                       `title` = '$title',                       `description` = '$description',                       `keywords` = '$keywords',                       `actual` = 1";        return Db::exec($sql);    }        /**     * Edit     *      * @param integer $id - category ID     * @param string $name - name     * @param string $text - about     * @param string $img - name of image     * @param string $title - Title     * @param string $description - Description     * @param string $keywords - Keywords      * @return boolean     */    public static function edit($id, $name, $text, $img, $title, $description, $keywords) {        $sql = "UPDATE `categories`                     SET `name` = '$name',                        `text` = '$text',                        `img` = '$img',                        `title` = '$title',                        `description` = '$description',                        `keywords` = '$keywords'                    WHERE `id` = $id";        return Db::exec($sql);    }        /**     * Delete     *      * @param $id - category ID     * @return boolean     */    public static function del($id) {                $sql = "DELETE FROM `categories` WHERE `id` = $id";        return Db::exec($sql);    }        /**     * Return one category     *      * @param $id - category ID     * @return array     */    public static function returnCategory($id) {        $sql = "SELECT * FROM `categories` WHERE `id` = $id";          $category = (Db::numRows($sql) > 0) ? Db::doOne($sql) : array();        return $category;    }        /**     * Return name of cover     *      * @param integer $id - category ID     * @return string     */    public static function returnCover($id)    {        $sql = "SELECT `img` FROM `categories` WHERE `id` = {$id}";        if (Db::numRows($sql) < 1)        {            $name = '';        }        else        {            $result = Db::doOne($sql);            $name = $result['img'];        }        return $name;    }}?>